document.getElementById("saveSettings")?.addEventListener("click",(()=>{const e=document.getElementById("darkMode").checked,t=document.getElementById("excludedSites").value.trim().split("\n").map((e=>e.trim())).filter((e=>e.length>0));console.log("Dark Mode:",e),console.log("Excluded Sites:",t),chrome.storage.sync.set({darkMode:e,excludedSites:t},(()=>{alert("Settings saved!"),console.log("Settings saved to chrome.storage")}))}));